// create random points with random z-values in their properties
var points = turf.random('point', 100, {
  bbox: [0, 30, 20, 50]
});
var numberOfClusters = 7;
var clustered = turf.clusters(points, numberOfClusters);

//addToMap
var addToMap = featureCollection(clustered.points);

var poly = turf.polygon([[[0,29],[3.5,29],[2.5,32],[0,29]]]);
var rotatedPoly = turf.rotate(poly, 100, [15, 15]);

//addToMap
rotatedPoly.properties = {stroke: '#F00', 'stroke-width': 4};
var addToMap = [poly, rotatedPoly];

var poly = turf.polygon([[[0,29],[3.5,29],[2.5,32],[0,29]]]);
var scaledPoly = turf.scale(poly, 3);

//addToMap
scaledPoly.properties = {stroke: '#F00', 'stroke-width': 4};
var addToMap = [poly, scaledPoly];

var poly = turf.polygon([[[0,29],[3.5,29],[2.5,32],[0,29]]]);
var translatedPoly = turf.translate(poly, 100, 35);

//addToMap
translatedPoly.properties = {stroke: '#F00', 'stroke-width': 4};
var addToMap = [poly, translatedPoly];
